--
-- 表的结构 `t_articles`
--

CREATE TABLE `t_articles` (
  `id` int(10) UNSIGNED NOT NULL,
  `category` int(11) UNSIGNED DEFAULT '0' COMMENT '一级分类',
  `tplType` tinyint(3) UNSIGNED DEFAULT '0',
  `title` varchar(120) CHARACTER SET utf8 NOT NULL COMMENT '标题',
  `image` varchar(120) CHARACTER SET utf8 DEFAULT '' COMMENT '文章库封面',
  `images` varchar(300) CHARACTER SET utf8 DEFAULT NULL,
  `keywords` varchar(60) CHARACTER SET utf8 DEFAULT NULL COMMENT '关键字',
  `description` varchar(160) CHARACTER SET utf8 DEFAULT NULL COMMENT '描述',
  `note` mediumtext CHARACTER SET utf8 COMMENT '内容',
  `adminid` int(10) UNSIGNED DEFAULT '0' COMMENT '管理员ID',
  `author` varchar(10) CHARACTER SET utf8 DEFAULT NULL COMMENT '作者',
  `tags` mediumtext CHARACTER SET utf8 COMMENT '标签',
  `source_url` varchar(120) CHARACTER SET utf8 DEFAULT '' COMMENT '来源网址',
  `status` tinyint(3) UNSIGNED DEFAULT '0' COMMENT '1审核通过\n2未审核',
  `listorder` int(10) UNSIGNED DEFAULT '0' COMMENT '排序',
  `intime` datetime DEFAULT NULL COMMENT '入库时间',
  `uptime` datetime DEFAULT NULL COMMENT '更新时间'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='文章库';

